    overflow-x: hidden;
    margin: 0;
    padding: 0;
  }

  body::before {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    background: 
      radial-gradient(circle at 20% 30%, rgba(139, 92, 246, 0.1) 0%, transparent 40%),
      radial-gradient(circle at 80% 20%, rgba(168, 85, 247, 0.08) 0%, transparent 40%),
      radial-gradient(circle at 60% 70%, rgba(59, 130, 246, 0.06) 0%, transparent 40%),
      radial-gradient(circle at 30% 80%, rgba(147, 51, 234, 0.08) 0%, transparent 40%);
    pointer-events: none;
    z-index: -10;
    animation: metaverse-float 20s ease-in-out infinite;
  }

  body::after {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    background: 
      conic-gradient(from 0deg at 50% 50%, transparent 0deg, rgba(139, 92, 246, 0.03) 60deg, transparent 120deg, rgba(168, 85, 247, 0.04) 180deg, transparent 240deg, rgba(59, 130, 246, 0.02) 300deg, transparent 360deg);
    pointer-events: none;
    z-index: -10;
    animation: metaverse-rotate 30s linear infinite;
  }

  html {
    scroll-behavior: smooth;
  }
}

@layer components {
  .noise-overlay {
    background-image: 
      radial-gradient(circle at 25% 25%, hsl(262, 83%, 58%) 0%, transparent 50%),
      radial-gradient(circle at 75% 75%, hsl(328, 86%, 58%) 0%, transparent 50%);
    opacity: 0.1;
    animation: noise 0.1s infinite;
  }

  .gradient-text {
    background: linear-gradient(45deg, hsl(262, 83%, 58%), hsl(328, 86%, 58%));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .glow-button {
    transition: all 0.3s ease;
  }

  .glow-button:hover {
    transform: translateY(-2px);
    box-shadow: 0 10px 25px hsla(262, 83%, 58%, 0.5);
  }

  .feature-card {
    background: linear-gradient(135deg, hsla(262, 83%, 58%, 0.1), hsla(328, 86%, 58%, 0.1));
    backdrop-filter: blur(10px);
    border: 1px solid hsla(262, 83%, 58%, 0.3);
  }

  .section-fade {
    opacity: 0;
    transform: translateY(30px);
    transition: all 0.8s ease-out;
  }

  .section-fade.in-view {
    opacity: 1;
    transform: translateY(0);
  }

  .font-anton {
    font-family: var(--font-anton);
  }

  .font-inter {
    font-family: var(--font-inter);
  }
}

@layer utilities {
  @keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-10px); }
  }

  @keyframes glow {
    from { box-shadow: 0 0 20px hsl(262, 83%, 58%); }
    to { box-shadow: 0 0 30px hsl(262, 83%, 58%), 0 0 40px hsl(262, 83%, 58%); }
  }

  @keyframes noise {
    0% { transform: translate(0, 0); }
    10% { transform: translate(-2px, -1px); }
    20% { transform: translate(-3px, 2px); }
    30% { transform: translate(1px, -2px); }
    40% { transform: translate(-1px, 3px); }
    50% { transform: translate(-2px, 1px); }
    60% { transform: translate(3px, 0); }
    70% { transform: translate(0, 2px); }
    80% { transform: translate(-3px, 0); }
    90% { transform: translate(2px, 1px); }
    100% { transform: translate(1px, 0); }
  }

  @keyframes organic-float {
    0%, 100% { transform: translateY(0px) rotate(0deg); }
    25% { transform: translateY(-8px) rotate(1deg); }
    50% { transform: translateY(-5px) rotate(0deg); }
    75% { transform: translateY(-12px) rotate(-1deg); }
  }

  @keyframes subtle-breath {
    0%, 100% { transform: scale(1) rotate(0deg); }
    50% { transform: scale(1.02) rotate(0.5deg); }
  }

  .animate-float {
    animation: float 3s ease-in-out infinite;
  }

  .animate-glow {
    animation: glow 2s ease-in-out infinite alternate;
  }

  .animate-noise {
    animation: noise 0.2s infinite;
  }

  .animate-organic-float {
    animation: organic-float 4s ease-in-out infinite;
  }

  .animate-subtle-breath {
    animation: subtle-breath 3s ease-in-out infinite;
  }

  .metaverse-bg {
    background: 
      radial-gradient(ellipse at top, rgba(139, 92, 246, 0.15) 0%, transparent 60%),
      radial-gradient(ellipse at bottom, rgba(168, 85, 247, 0.1) 0%, transparent 60%),
      linear-gradient(45deg, rgba(59, 130, 246, 0.05) 0%, transparent 50%, rgba(147, 51, 234, 0.05) 100%);
  }

  @keyframes metaverse-float {
    0%, 100% { 
      transform: translateY(0px) scale(1); 
      opacity: 0.6;
    }
    33% { 
      transform: translateY(-20px) scale(1.05); 
      opacity: 0.8;
    }
    66% { 
      transform: translateY(10px) scale(0.95); 
      opacity: 0.7;
    }
  }

  @keyframes metaverse-rotate {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
  }

  /* Text Animation Effects */
  @keyframes typewriter {
    from { width: 0; }
    to { width: 100%; }
  }

  @keyframes blinkCursor {
    0%, 50% { border-right-color: transparent; }
    51%, 100% { border-right-color: #8B5CF6; }
  }

  @keyframes textReveal {
    0% {
      opacity: 0;
      transform: translateY(30px);
    }
    100% {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes textGlow {
    0%, 100% {
      text-shadow: 0 0 10px rgba(139, 92, 246, 0.5),
                   0 0 20px rgba(139, 92, 246, 0.3),
                   0 0 30px rgba(139, 92, 246, 0.2);
    }
    50% {
      text-shadow: 0 0 20px rgba(139, 92, 246, 0.8),
                   0 0 30px rgba(139, 92, 246, 0.6),
                   0 0 40px rgba(139, 92, 246, 0.4);
    }
  }

  @keyframes slideInLeft {
    0% {
      opacity: 0;
      transform: translateX(-50px);
    }
    100% {
      opacity: 1;
      transform: translateX(0);
    }
  }

  @keyframes slideInRight {
    0% {
      opacity: 0;
      transform: translateX(50px);
    }
    100% {
      opacity: 1;
      transform: translateX(0);
    }
  }

  @keyframes fadeInUp {
    0% {
      opacity: 0;
      transform: translateY(40px);
    }
    100% {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes scaleIn {
    0% {
      opacity: 0;
      transform: scale(0.8);
    }
    100% {
      opacity: 1;
      transform: scale(1);
    }
  }

  /* Text Animation Classes */
  .typewriter-text {
    overflow: hidden;
    white-space: nowrap;
    border-right: 3px solid #8B5CF6;
    animation: typewriter 3s steps(40) forwards, blinkCursor 1s infinite;
  }

  .text-reveal {
    opacity: 0;
    animation: textReveal 0.8s ease-out forwards;
  }

  .text-glow {
    animation: textGlow 2s ease-in-out infinite;
  }

  .slide-in-left {
    opacity: 0;
    animation: slideInLeft 0.8s ease-out forwards;
  }

  .slide-in-right {
    opacity: 0;
    animation: slideInRight 0.8s ease-out forwards;
  }

  .fade-in-up {
    opacity: 0;
    animation: fadeInUp 0.8s ease-out forwards;
  }

  .scale-in {
    opacity: 0;
    animation: scaleIn 0.6s ease-out forwards;
  }

  .animated-delay-1 { animation-delay: 0.2s; }
  .animated-delay-2 { animation-delay: 0.4s; }
  .animated-delay-3 { animation-delay: 0.6s; }
  .animated-delay-4 { animation-delay: 0.8s; }
  .animated-delay-5 { animation-delay: 1.0s; }

  @keyframes bounce-slow {
    0%, 100% { 
      transform: translateY(0px) rotate(0deg); 
    }
    50% { 
      transform: translateY(-20px) rotate(5deg); 
    }
  }

  .animate-bounce-slow {
    animation: bounce-slow 3s ease-in-out infinite;
  }
}

